grid = input.splitlines()[1:]



def count_xmas_occurrences(grid, word="XMAS"):
    rows = len(grid)
    cols = len(grid[0])
    word_length = len(word)
    directions = [
        (0, 1),  # Right
        (0, -1), # Left
        (1, 0),  # Down
        (-1, 0), # Up
        (1, 1),  # Diagonal down-right
        (-1, 1), # Diagonal up-right
        (1, -1), # Diagonal down-left
        (-1, -1) # Diagonal up-left
    ]
    
    count = 0

    def is_valid(x, y):
        return 0 <= x < rows and 0 <= y < cols

    for r in range(rows):
        for c in range(cols):
            if grid[r][c] == word[0]:  # Only start if first letter matches
                for dr, dc in directions:
                    found = True
                    for k in range(word_length):
                        nr, nc = r + dr * k, c + dc * k
                        if not (is_valid(nr, nc) and grid[nr][nc] == word[k]):
                            found = False
                            break
                    if found:
                        count += 1
    
    return count

# print(grid)
grid = [list(row) for row in grid]
# print(grid)
print(count_xmas_occurrences(grid))
